#import "BraintreeViewController.h"
#import "BraintreeCore.h"
#import "BraintreeDropIn.h"

/**
 * Online Documentation
 * Custom UI: https://developers.braintreepayments.com/guides/credit-cards/client-side/ios/v4
 * Drop-In UI: https://developers.braintreepayments.com/start/hello-client/ios/v4
 * Sample Credit Card: 4111 1111 1111 1111
 * Needs to setup own server and deployment target requirment is 9.0
 */

@interface BraintreeViewController ()

@end

@implementation BraintreeViewController

- (void)viewDidLoad {
    [super viewDidLoad];
    
    // Sample client token from Braintree for testing
    NSString *clientToken = @"";
    
    // Show the Braintree Drop-In UI
    [self showDropIn:clientToken];

}

#pragma mark - Braintree

- (void)showDropIn:(NSString *)clientTokenOrTokenizationKey {
    BTDropInRequest *request = [[BTDropInRequest alloc] init];
    BTDropInController *dropIn = [[BTDropInController alloc] initWithAuthorization:clientTokenOrTokenizationKey request:request handler:^(BTDropInController * _Nonnull controller, BTDropInResult * _Nullable result, NSError * _Nullable error) {
        
        if (error != nil) {
            DLog(@"Braintree: Error");
        } else if (result.cancelled) {
            DLog(@"Braintree: Cancelled");
        } else {
            DLog(@"Braintree: Success");

            // Use the BTDropInResult properties to update your UI
            // result.paymentOptionType
            // result.paymentMethod
            // result.paymentIcon
            // result.paymentDescription
        }
        
        [controller dismissViewControllerAnimated:YES completion:nil];
    }];
    [self presentViewController:dropIn animated:YES completion:nil];
}

@end
